scene.onOverlapTile(SpriteKind.PlatformerPlayer, assets.tile`myTile31`, function (sprite, location) {
    tiles.setTileAt(location, assets.tile`myTile0`)
    controller.moveSprite(mySprite, 0, 0)
    sciencePlatformer = false
    platformer = false
    tiles.placeOnTile(mySprite, location)
    timer.after(2000, function () {
        color.startFade(color.originalPalette, color.Black, 1000)
        timer.after(3000, function () {
            tiles.placeOnTile(mySprite, tiles.getTileLocation(3, 3))
            tiles.setTileAt(tiles.getTileLocation(2, 3), assets.tile`myTile34`)
            music.play(music.melodyPlayable(music.baDing), music.PlaybackMode.InBackground)
            scene.cameraFollowSprite(mySprite)
            color.startFade(color.Black, color.originalPalette, 500)
            mySprite.sayText("Great! My very own dinosaur egg.", 5000, false)
            timer.after(5000, function () {
                mySprite.sayText("To The present!", 2000, true)
                timer.after(2000, function () {
                    sprites.destroy(mySprite)
                    tiles.setTileAt(tiles.getTileLocation(2, 3), assets.tile`transparency16`)
                    music.play(music.melodyPlayable(music.knock), music.PlaybackMode.InBackground)
                    tiles.setTileAt(tiles.getTileLocation(1, 3), assets.tile`myTile25`)
                    timer.after(1000, function () {
                        music.play(music.melodyPlayable(music.knock), music.PlaybackMode.InBackground)
                        tiles.setTileAt(tiles.getTileLocation(1, 3), assets.tile`myTile18`)
                        timer.after(1000, function () {
                            music.play(music.createSoundEffect(
                            WaveShape.Noise,
                            1,
                            5000,
                            0,
                            255,
                            6100,
                            SoundExpressionEffect.None,
                            InterpolationCurve.Linear
                            ), music.PlaybackMode.InBackground)
                            color.startFade(color.originalPalette, color.Adafruit, 200)
                            timer.after(200, function () {
                                color.startFade(color.Adafruit, color.Matte, 200)
                                timer.after(200, function () {
                                    color.startFade(color.Matte, color.DIY, 200)
                                    timer.after(200, function () {
                                        color.startFade(color.DIY, color.Sweet, 200)
                                        timer.after(200, function () {
                                            color.startFade(color.Sweet, color.Adventure, 200)
                                            timer.after(200, function () {
                                                color.startFade(color.Adventure, color.White, 200)
                                                timer.after(5000, function () {
                                                    blockSettings.writeNumber("Level", 5)
                                                    game.reset()
                                                })
                                            })
                                        })
                                    })
                                })
                            })
                        })
                    })
                })
            })
        })
    })
})
