scene.onHitWall(SpriteKind.PlatformerPlayer, function (sprite, location) {
    if (tiles.tileAtLocationEquals(location, assets.tile`myTile7`)) {
        music.play(music.melodyPlayable(music.thump), music.PlaybackMode.InBackground)
        timer.after(300, function () {
            music.play(music.melodyPlayable(music.smallCrash), music.PlaybackMode.InBackground)
            tiles.setTileAt(location, assets.tile`myTile0`)
            tiles.setWallAt(location, false)
            timer.after(2000, function () {
                tiles.setTileAt(location, assets.tile`myTile7`)
                tiles.setWallAt(location, true)
            })
        })
    }
    if (tiles.tileAtLocationEquals(location, assets.tile`myTile6`)) {
        game.gameOver(false)
    }
})
