game.onUpdate(function () {
    if (platformer == true) {
        if (tiles.tileAtLocationEquals(mySprite.tilemapLocation(), sprites.swamp.swampTile9) || tiles.tileAtLocationEquals(mySprite.tilemapLocation(), sprites.swamp.swampTile13)) {
            mySprite.vy = 30
            controller.moveSprite(mySprite, 55, 0)
        }
        if (theTree == false) {
            if (sciencePlatformer == false) {
                if (mySprite.vy == 0 && controller.A.isPressed() && mySprite.isHittingTile(CollisionDirection.Bottom)) {
                    mySprite.vy = -150
                } else {
                    mySprite.vy += 10
                }
            } else {
                if (!(tiles.tileAtLocationEquals(mySprite.tilemapLocation(), assets.tile`tile11`) || tiles.tileAtLocationEquals(mySprite.tilemapLocation(), assets.tile`tile14`))) {
                    controller.moveSprite(mySprite, 85, 0)
                    if (mySprite.vy == 0 && controller.A.isPressed() && mySprite.isHittingTile(CollisionDirection.Bottom)) {
                        mySprite.vy = -200
                    } else {
                        mySprite.vy += 10
                    }
                } else {
                    mySprite.vy = 30
                    controller.moveSprite(mySprite, 45, 0)
                }
            }
        } else {
            if (mySprite.vy == 0 && controller.A.isPressed() && mySprite.isHittingTile(CollisionDirection.Bottom)) {
                mySprite.vy = -170
            } else {
                mySprite.vy += 6
            }
        }
    }
})
